package operators;

public class Logical_and_or {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean bool1 = true, bool2 = false;//Variables Definition and Initialization

		  
		  System.out.println("bool1 && bool2 = " + (bool1 && bool2));//Logical AND
		  
		  
		  System.out.println("bool1 || bool2 = " + (bool1 | bool2) );//Logical OR
		  
		  
		  System.out.println("!(bool1 && bool2) = " + !(bool1 && bool2));//Logical Not

	}

}
